import { useState } from 'react';
import './App.css';
import Titulo from './components/Titulo';
import Selectores from './components/Selectores';
import BotonCalcular from './components/BotonCalcular';
import Resultados from './components/Resultados';

function App() {

  const [calculado, setCalculado] = useState(false);
  const [selectores, setSelectores] = useState(false);
  const [lambdaLocal, setLambdaLocal] = useState(0);
  const [lambdaVisitante, setLambdaVisitante] = useState(0);
  const [lambdaArbitro, setLambdaArbitro] = useState(0);


  function handleSelectores(select){
    setSelectores(select);
  }

  function handleCalcular(){
    setCalculado(true);
  }

  function handleLambda(l1, l2, l3){
    setLambdaLocal(l1);
    setLambdaVisitante(l2);
    setLambdaArbitro(l3);
  }







  return (
    <div className="App">
      <Titulo />
      <Selectores handleSelectores = {handleSelectores}/>
      <BotonCalcular handleCalcular = {handleCalcular}/>
      {calculado && selectores ? <Resultados /> : null}
    </div>  
  );
}

export default App;
